import * as React from "react";
import ImagesList from "./ImagesList";
import * as AtomicBlockUtils from "draft-js/lib/AtomicBlockUtils";
import Modal from "./Modal";

class RichTextImagePicker extends React.Component {
  addImage(image) {
    const { editorState, onChange } = this.props;
    const entityData = {
      src: image.src,
      height: "auto",
      width: "auto",
    };

    const entityKey = editorState
      .getCurrentContent()
      .createEntity("IMAGE", "MUTABLE", entityData)
      .getLastCreatedEntityKey();

    const newEditorState = AtomicBlockUtils.insertAtomicBlock(
      editorState,
      entityKey,
      " "
    );
    onChange(newEditorState);
    this.refs.pickerModal.close();
  }

  render() {
    const modal = (
      <Modal ref="pickerModal">
        <ImagesList onSelect={this.addImage.bind(this)} />
      </Modal>
    );

    return (
      <div className="rdw-link-wrapper" aria-label="rdw-link-control">
        <div
          className="rdw-option-wrapper"
          title="Image"
          onClick={() => {
            this.refs.pickerModal.open();
          }}
        >
          <img
            src="data:image/svg+xml;base64,"
            alt=""
          />
        </div>
        {modal}
      </div>
    );
  }
}

export default RichTextImagePicker;
